mapper.select:
select pi.product_id, pi.img_url from product_image pi inner join (select product_id, min(sort_order) sort_order from product_image where product_id in
#{productId,jdbcType=INTEGER}
and is_primary = 1 group by product_id) temp on pi.product_id = temp.product_id and pi.sort_order = temp.sort_order where pi.is_primary = 1;
