mapper.select:
select p.id as productId,p.product_name as productName,pp.sell_price as sellPrice from product p inner join product_price pp on pp.product_id=p.id where p.is_deleted = false and p.product_type = 2 and p.product_status=1 and p.category_id in
#{model.categoryIdList[${index}]}
order by p.on_shelf_time ${model.ascText}

select p.id as productId,p.product_name as productName,pp.sell_price as sellPrice from product p inner join product_price pp on pp.product_id=p.id left join product_summary ps on ps.product_id=p.id where p.is_deleted = false and p.product_type = 2 and p.product_status=1 and p.category_id in
#{model.categoryIdList[${index}]}
order by ps.total_sold_qty ${model.ascText}

select p.id as productId,p.product_name as productName,pp.sell_price as sellPrice from product p inner join product_price pp on pp.product_id=p.id where p.is_deleted = false and p.product_type = 2 and p.product_status=1 and p.category_id in
#{model.categoryIdList[${index}]}
order by pp.sell_price ${model.ascText}
