mapper.select:
select

from end_user_session
where end_user_id = #{endUserId,jdbcType=INTEGER}
and login_channel = #{loginChannel, jdbcType=TINYINT}

select

from end_user_session
where session_token = #{sessionToken,jdbcType=VARCHAR}
and login_channel = #{loginChannel, jdbcType=TINYINT}
and last_refresh_time > #{mustRefreshAfter, jdbcType=TIMESTAMP}
and is_logout != 1

update end_user_session
set last_refresh_time = now()
where end_user_id = #{endUserId,jdbcType=VARCHAR}
and login_channel = #{loginChannel, jdbcType=TINYINT}

mapper.update:
update end_user_session
set is_logout = true,
logout_time = now()
where end_user_id = #{endUserId,jdbcType=INTEGER}
and login_channel = #{loginChannel,jdbcType=TINYINT}
