mapper.select:
select 

from end_user_session
where id = #{id,jdbcType=INTEGER}
  and end_user_id = #{endUserId,jdbcType=INTEGER}
  and login_channel = #{loginChannel,jdbcType=TINYINT}

mapper.delete:
delete from end_user_session
where id = #{id,jdbcType=INTEGER}
  and end_user_id = #{endUserId,jdbcType=INTEGER}
  and login_channel = #{loginChannel,jdbcType=TINYINT}

mapper.update:
update end_user_session
set session_token = #{sessionToken,jdbcType=VARCHAR},
  user_agent_info = #{userAgentInfo,jdbcType=VARCHAR},
  last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP},
  last_login_ip = #{lastLoginIp,jdbcType=BIGINT},
  last_refresh_time = #{lastRefreshTime,jdbcType=TIMESTAMP},
  is_logout = #{isLogout,jdbcType=BIT},
  logout_time = #{logoutTime,jdbcType=TIMESTAMP},
  is_active = #{isActive,jdbcType=BIT}
where id = #{id,jdbcType=INTEGER}
  and end_user_id = #{endUserId,jdbcType=INTEGER}
  and login_channel = #{loginChannel,jdbcType=TINYINT}
